INSERT INTO `wp_posts` VALUES (7,1,'2023-06-03 18:09:08','2023-06-03 16:09:08','<!-- wp:heading {\"textAlign\":\"center\",\"level\":1,\"style\":{\"color\":{\"text\":\"#3b8e46\"}},\"fontSize\":\"large\"} -->\n<h1 class=\"wp-block-heading has-text-align-center has-text-color has-large-font-size\" style=\"color:#3b8e46\">BOISSON ÉNERGISANTE<br>100% NATURELLE</h1>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<div class=\"wp-block-uagb-image aligncenter uagb-block-9de4cf20 wp-block-uagb-image--layout-default wp-block-uagb-image--effect-static wp-block-uagb-image--align-center\"><figure class=\"wp-block-uagb-image__figure\"><img srcset=\"http://localhost:81/planty/wp-content/uploads/2023/07/Planty6-1-1-387x600.png 387w\" sizes=\"(max-width: 387px) 150px\" src=\"http://localhost:81/planty/wp-content/uploads/2023/07/Planty6-1-1-387x600.png\" alt=\"\" class=\"uag-image-301\" width=\"387\" height=\"618\" title=\"\" loading=\"lazy\"/></figure></div>\n<!-- /wp:html -->\n\n<!-- wp:group {\"className\":\"background-image-container\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group background-image-container\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3b8e46\"}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" id=\"lenergie\" style=\"color:#3b8e46\">L’énergie des plantes</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"className\":\"planty\"} -->\n<p class=\"has-text-align-center planty\">Planty, c’est la première boisson<br>énergisante composée à 100 % de produits naturels.<br>Avec son goût frais et fruité, retrouvez votre<br>énergie grâce aux plantes riches en vitamine B2,<br>à n’importe quel moment de la journée.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"color\":{\"text\":\"#d2776a\"},\"typography\":{\"textTransform\":\"uppercase\"}},\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading has-t